ENT.Type = "anim"ENT.Base = "hzn_production_ent" ENT.PrintName		= "Remote Suitcharger"ENT.Author			= "Bynari"ENT.AutomaticFrameAdvance = true-- item descriptionlocal desc = {}	desc[1] = "This device replenishes your suits resources within a short range."	desc[2] = "Requires a constant amount of energy (50 units per second) to operate."-- production requirementslocal req = {}local req = {}	req["morphite"] = 100	req["nocxium"] = 100	req["isogen"] = 0-- factory entrylocal Entry = {}	Entry.DisplayName = ENT.PrintName	Entry.Description = desc	Entry.ClassName = "remote_suitcharger"	Entry.BuildTime = 10	Entry.Costs = req	-- register entry with gamemodeif GAMEMODE:GetGameDescription() == "Horizon" then	GAMEMODE:RegisterFactoryEntry( Entry )end