ENT.Type = "anim"ENT.Base = "hzn_production_ent" ENT.PrintName		= "Water Pump"ENT.Author			= "Bynari"ENT.AutomaticFrameAdvance = true -- item descriptionlocal desc = {}	desc[1] = "Pumps water out of a lake or ocean at a rate of (15 units per second)."	desc[2] = "Requires a constant amount of energy (60 units per second to operate)."	desc[3] = "Needs to be submerged in water."-- production requirementslocal req = {}	req["morphite"] = 0	req["nocxium"] = 0	req["isogen"] = 0	-- factory entrylocal Entry = {}	Entry.DisplayName = ENT.PrintName	Entry.Description = desc	Entry.ClassName = "water_pump"	Entry.BuildTime = 10	Entry.Costs = req	-- register entry with gamemodeif GAMEMODE:GetGameDescription() == "Horizon" then	GAMEMODE:RegisterFactoryEntry( Entry )end